mapper.select:
SELECT doctorId
FROM doctor
WHERE dOffice = #{department} AND dName=#{dName} AND dTitle = "专家";

mapper.update:
UPDATE doctor
SET patientId=#{patientId}
WHERE doctorId=#{doctorId}

mapper.select:
SELECT patientId
FROM doctor
WHERE dOffice = #{department} AND dTitle = "普通";

