mapper.update:
UPDATE item
SET itemHaveDone=1
WHERE patientId = #{patientId} AND itemName=#{itemName};

mapper.select:
SELECT *
FROM item
WHERE havePay = 1  AND itemHaveDone=0;

SELECT itemDate
FROM item
WHERE patientId = #{patientId} AND itemName=#{itemName} AND havePay = 1 AND  medHaveDone = 1;

SELECT *
FROM item
WHERE havePay = 1  AND itemHaveDone=0 AND pIdentificationNum=#{pIdentificationNum};
