mapper.update:
UPDATE record
SET medHaveDone=1
WHERE patientId = #{patientId} AND medName=#{medName};

UPDATE record
SET medHaveDone=0
WHERE patientId = #{patientId} AND medName=#{medName};

mapper.select:
SELECT *
FROM record
WHERE havePay = 1 AND  medHaveDone=0;

SELECT *
FROM record
WHERE havePay = 1 AND  medHaveDone=1;

SELECT recordDate
FROM record
WHERE patientId = #{patientId} AND medName=#{medName} AND havePay = 1 AND  medHaveDone = 1;

SELECT medHaveDone
FROM record
WHERE patientId = #{patientId};

SELECT *
FROM record
WHERE havePay = 1 AND  medHaveDone=0 AND pIdentificationNum=#{pIdentificationNum};

SELECT *
FROM record
WHERE havePay = 1 AND  medHaveDone=1 AND pIdentificationNum=#{pIdentificationNum};

SELECT *
FROM record
WHERE patientId = #{patientId} AND medName=#{medName} AND havePay = 1 AND  medHaveDone = 1;
